'use client';

import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { WeatherData } from '@/lib/types';

export default function WeatherPage() {
  const [weatherData, setWeatherData] = useState<WeatherData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchWeather() {
      try {
        const response = await fetch('/api/weather');
        if (!response.ok) {
          throw new Error('Failed to fetch weather data');
        }
        const data = await response.json();
        setWeatherData(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load weather data');
      } finally {
        setLoading(false);
      }
    }

    fetchWeather();
  }, []);

  const getWeatherIcon = (condition: string) => {
    const icons: Record<string, string> = {
      'Clear': '‚òÄÔ∏è',
      'Clouds': '‚òÅÔ∏è',
      'Rain': 'üåßÔ∏è',
      'Drizzle': 'üå¶Ô∏è',
      'Thunderstorm': '‚õàÔ∏è',
      'Snow': 'üå®Ô∏è',
      'Mist': 'üå´Ô∏è',
      'Sunny': '‚òÄÔ∏è',
      'Partly Cloudy': '‚õÖ',
      'Cloudy': '‚òÅÔ∏è',
      'Light Rain': 'üå¶Ô∏è',
      'Overcast': '‚òÅÔ∏è',
      'Hot': 'üå°Ô∏è'
    };
    return icons[condition] || 'üå§Ô∏è';
  };

  if (loading) {
    return (
      <div className="py-20 flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading weather data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="py-20">
        <div className="container mx-auto px-4 lg:px-6">
          <div className="text-center">
            <h1 className="text-4xl font-bold mb-4">Weather in Indore</h1>
            <div className="bg-destructive/10 border border-destructive/20 rounded-lg p-6 max-w-md mx-auto">
              <p className="text-destructive">Unable to load weather data. Please try again later.</p>
              <p className="text-sm text-muted-foreground mt-2">{error}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="py-20">
      <div className="container mx-auto px-4 lg:px-6">
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-4xl font-bold mb-4">Weather in Indore</h1>
          <p className="text-muted-foreground text-lg">Current weather conditions and forecast for planning your visit</p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 max-w-4xl mx-auto">
          {/* Current Weather */}
          <motion.div
            className="bg-card border border-border rounded-lg p-8 text-center"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <div className="mb-6">
              <div className="text-6xl mb-4">{getWeatherIcon(weatherData?.condition || 'Clear')}</div>
              <h3 className="text-3xl font-bold mb-2" data-testid="current-temperature">
                {weatherData?.temperature || 28}¬∞C
              </h3>
              <p className="text-muted-foreground" data-testid="current-condition">
                {weatherData?.condition || 'Partly Cloudy'}
              </p>
            </div>

            <div className="grid grid-cols-2 gap-4 text-sm">
              <div className="bg-secondary border border-border rounded-lg p-4">
                <svg className="w-5 h-5 text-primary mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                </svg>
                <p className="text-muted-foreground">Visibility</p>
                <p className="font-semibold" data-testid="visibility">
                  {weatherData?.visibility || 10} km
                </p>
              </div>
              <div className="bg-secondary border border-border rounded-lg p-4">
                <svg className="w-5 h-5 text-primary mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 7.172V5L8 4z" />
                </svg>
                <p className="text-muted-foreground">Humidity</p>
                <p className="font-semibold" data-testid="humidity">
                  {weatherData?.humidity || 65}%
                </p>
              </div>
              <div className="bg-secondary border border-border rounded-lg p-4">
                <svg className="w-5 h-5 text-primary mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 4V2a1 1 0 011-1h8a1 1 0 011 1v2m-9 10v6a1 1 0 001 1h8a1 1 0 001-1v-6M7 14h10" />
                </svg>
                <p className="text-muted-foreground">Wind Speed</p>
                <p className="font-semibold" data-testid="wind-speed">
                  {weatherData?.windSpeed || 12} km/h
                </p>
              </div>
              <div className="bg-secondary border border-border rounded-lg p-4">
                <svg className="w-5 h-5 text-primary mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
                <p className="text-muted-foreground">Feels Like</p>
                <p className="font-semibold" data-testid="feels-like">
                  {weatherData?.feelsLike || 31}¬∞C
                </p>
              </div>
            </div>
          </motion.div>

          {/* 7-Day Forecast */}
          <motion.div
            className="bg-card border border-border rounded-lg p-8"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <h3 className="text-xl font-semibold mb-6">7-Day Forecast</h3>
            <div className="space-y-4">
              {(weatherData?.forecast || [
                { day: 'Today', icon: '01d', condition: 'Sunny', high: 32, low: 24 },
                { day: 'Tomorrow', icon: '02d', condition: 'Cloudy', high: 29, low: 22 },
                { day: 'Friday', icon: '10d', condition: 'Light Rain', high: 26, low: 20 },
                { day: 'Saturday', icon: '01d', condition: 'Sunny', high: 31, low: 23 },
                { day: 'Sunday', icon: '02d', condition: 'Partly Sunny', high: 30, low: 22 },
                { day: 'Monday', icon: '03d', condition: 'Overcast', high: 28, low: 21 },
                { day: 'Tuesday', icon: '01d', condition: 'Sunny', high: 33, low: 25 }
              ]).map((day, index) => (
                <motion.div
                  key={day.day}
                  className="flex items-center justify-between py-2 border-b border-border last:border-b-0"
                  initial={{ opacity: 0, x: 30 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  data-testid={`forecast-${index}`}
                >
                  <div className="flex items-center gap-3">
                    <span className="text-2xl">{getWeatherIcon(day.condition)}</span>
                    <span className="font-medium">{day.day}</span>
                  </div>
                  <div className="flex items-center gap-4">
                    <span className="text-muted-foreground">{day.condition}</span>
                    <div className="text-right">
                      <span className="font-semibold">{day.high}¬∞</span>
                      <span className="text-muted-foreground ml-2">{day.low}¬∞</span>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </div>

        {/* Weather Tips */}
        <motion.div
          className="mt-12 bg-primary/10 border border-primary/20 rounded-lg p-6 max-w-2xl mx-auto text-center"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <svg className="w-8 h-8 text-primary mx-auto mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
          </svg>
          <h3 className="text-lg font-semibold mb-2">Weather Tips</h3>
          <p className="text-muted-foreground">Perfect weather for exploring Indore's outdoor attractions! Don't forget to stay hydrated and carry sunscreen.</p>
        </motion.div>
      </div>
    </div>
  );
}

